import React, { useState } from 'react';
import axios from 'axios'
import '../../App.css';
import Button from '../Button';
import Card from '../Card';

function VigenereEncrypt() {
    const [response, setResponse] = useState({})
    const [plaintext, setPlaintext] = useState({
        text: '',
        key: ''
    })

    const handleChange = (event) => {
        const { name, value } = event.target;
        setPlaintext({
            ...plaintext,
            [name]: value
        });
    };


    const handleSubmit = (event) => {
        event.preventDefault();
        const path = 'http://127.0.0.1:5000/vigenere_encrypt'
        // const path = 'https://cryptography-web-application.herokuapp.com/vigenere_encrypt'
        axios({
            method: 'POST',
            url: path,
            data: {
                plaintext: plaintext.text,
                key: plaintext.key
            }
        })
            .then(response => {
                console.log("SUCCESS", response)
                setResponse(response)
            }).catch(error => {
                console.log(error)
            })
    }

    return (
        <div>
            <h3>Encryption</h3>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="">Plaintext:
                        <input
                            type="text"
                            name="plaintext"
                            id="plain-text"
                            value={plaintext.text}
                            onChange={handleChange} />
                    </label>
                </div>
                <div>
                    <label htmlFor="">Key:
                        <input
                            type="text"
                            name="key"
                            id="key-value"
                            value={plaintext.key}
                            onChange={handleChange} />
                    </label>
                </div>
                <Button name={'Encrypt'} />
            </form>

            <div className='text'>{response.status === 200 &&
                <div >
                    <h4>Encrypted Text:</h4>
                    <p>{response.data.ciphertext}</p>
                </div>}
            </div>
        </div>


    );
}

export default VigenereEncrypt;
