import React, { useState } from 'react';
import axios from 'axios'
import '../../App.css';
import Button from '../Button';

function ShiftDecrypt() {
    const [decryptedText, setDecryptedText] = useState({})
    const [shiftDecrypt, setShiftDecrypt] = useState({
        ciphertext: '',
        key: null
    })

    const handleChange = (event) => {
        const { name, value } = event.target;
        setShiftDecrypt({
            ...shiftDecrypt,
            [name]: value
        });
    };


    const handleSubmit = (event) => {
        event.preventDefault();
        const path = ""
        if (shiftDecrypt.key == null) {
            path = 'http://127.0.0.1:5000/shift_encrypt_nokey'
            // path = 'https://cryptography-web-application.herokuapp.com/shift_decrypt_nokey'
        }
        else {
            path = 'http://127.0.0.1:5000/shift_encrypt'
            // path = 'https://cryptography-web-application.herokuapp.com/shift_decrypt'
        }

        axios({
            method: 'POST',
            url: path,
            data: {
                ciphertext: shiftDecrypt.ciphertext,
                key: shiftDecrypt.key
            }
        })
            .then(response => {
                console.log("SUCCESS", response)
                setDecryptedText(response)
            }).catch(error => {
                console.log(error)
            })
    }

    return (
        <div>
            <h3>Decryption</h3>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="">Ciphertext:
                        <input
                            type="text"
                            name="ciphertext"
                            id="cipher-text"
                            value={shiftDecrypt.ciphertext}
                            onChange={handleChange} />
                    </label>
                </div>
                <div>
                    <label htmlFor="">Key:
                        <input
                            type="number"
                            pattern='[0-9]'
                            name="key"
                            id="key-num"
                            value={shiftDecrypt.key}
                            onChange={handleChange} />
                    </label>
                </div>
                <Button name={'Decrypt'} />
            </form>

            <div className='text'>{decryptedText.status === 200 &&
                <div>
                    <h4>Decrypted Text:</h4>
                    <p>{decryptedText.data.plaintext}</p>
                </div>}
            </div>

        </div>
    );
}

export default ShiftDecrypt;
