import './App.css';
import React, { useEffect, useState } from 'react';
import axios from 'axios'

function App() {
  const [message, setMessage] = useState({})
  const [ciphertext, setCipertext] = useState({})

  // useEffect(() => {
  //   axios.get('https://cryptography-web-application.herokuapp.com/flask/crypto').then(response => {
  //     console.log("SUCCESS", response)
  //     setGetMessage(response)
  //   }).catch(error => {
  //     console.log(error)
  //   })

  let handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let res = await fetch("https://httpbin.org/post", {
        method: "get",
        body: JSON.stringify({
          ciphertext: ciphertext,

        }),
      });
      let resJson = await res.json();
      if (res.status === 200) {
        setCipertext("");

      } else {
        setMessage("Some error occured");
      }
    } catch (err) {
      console.log(err);
    }
  };


  useEffect(() => {
    axios.get('http://127.0.0.1:5000/flask/crypto').then(response => {
      console.log("SUCCESS", response)
      setMessage(response)
    }).catch(error => {
      console.log(error)
    })

  }, [])
  return (
    <div className="App">
      <header className="App-header">
        <h1>Cryptography Web App</h1>
        <div>{message.status === 200 ?
          <h3>{message.data.message}</h3>
          :
          <h3>LOADING</h3>}</div>
        <form>
          <label htmlFor="">
            Enter Ciphertext to decrypt

          </label>
          <input type="text" value={ciphertext.value} />
        </form>
      </header>

    </div>
  );
}

export default App;
