import React, { useState } from 'react';
import axios from 'axios'
import '../../App.css';
import Button from '../Button';
import Card from '../Card';

function ShiftDecrypt() {
    const [decryptedText, setDecryptedText] = useState({})
    const [encryptedText, setEncryptedText] = useState({
        text: "",
        key: ""
    })

    const handleChange = (event) => {
        const { name, value } = event.target;
        setEncryptedText({
            ...encryptedText,
            [name]: value
        });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const path = 'http://127.0.0.1:5000/shift_decrypt'
        // const path = 'https://cryptography-web-application.herokuapp.com/shift_decrypt'
        axios({
            method: 'POST',
            url: path,
            data: {
                ciphertext: encryptedText.text,
                key: encryptedText.key
            }
        })
            .then(response => {
                console.log("SUCCESS", response)
                setDecryptedText(response)
            }).catch(error => {
                console.log(error)
            })
    }

    return (
        <div>
            <h3>Decryption</h3>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="">Ciphertext:
                        <input
                            type="text"
                            name="ciphertext"
                            id="cipher-text"
                            value={encryptedText.text}
                            onChange={handleChange} />
                    </label>
                </div>
                <div>
                    <label htmlFor="">Key:
                        <input
                            type="number"
                            pattern='[0-9]'
                            name="key"
                            id="key-num"
                            value={encryptedText.key}
                            onChange={handleChange} />
                    </label>
                </div>
                <Button name={'Decrypt'} />
            </form>

            <div className='text'>
                {decryptedText.status === 200
                    ? <h4>Decrypted Text:</h4> &&
                    <Card text={decryptedText.data.plaintext} shiftKey={encryptedText.key} key={encryptedText.key} />
                    : <h4>Decrypted Text:</h4> &&
                    <div>
                        {JSON.parse(decryptedText.data.decryption_list).map((decryption) =>
                            <Card text={decryption['text']} shiftKey={decryption['key']} key={decryption['key']} />)
                        }
                    </div>
                }
            </div>
        </div>
    );
}

export default ShiftDecrypt;
