import './App.css';
import React, { useEffect, useState } from 'react';
import axios from 'axios'
import Button from './components/Button';

function App() {
  // const [message, setMessage] = useState({})
  const [cipherText, setCipherText] = useState({
    cipher_text: ''
  })

  const [shiftEncrypt, setShiftEncrypt] = useState({
    plaintext: '',
    shift: 0
  })

  const handleChange = (event) => {
    const { name, value } = event.target;
    setShiftEncrypt({
      ...shiftEncrypt,
      [name]: value
    });
  };


  const handleSubmit = (event) => {
    event.preventDefault();
    const path = 'http://127.0.0.1:5000/shift_encrypt'
    // const path = 'https://cryptography-web-application.herokuapp.com/shift_decrypt'
    axios({
      method: 'POST',
      url: path,
      data: {
        plaintext: shiftEncrypt.plaintext,
        shift: shiftEncrypt.shift
      }
    })
      .then(response => {
        console.log("SUCCESS", response)
        setCipherText(response)
        console.log(response)
      }).catch(error => {
        console.log('error')
        console.log(error)
      })
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Cryptography Web App</h1>
        <form onSubmit={handleSubmit}>
          <label htmlFor="">Plaintext:
            <input
              type="text"
              name="plaintext"
              id="plain-text"
              value={shiftEncrypt.plaintext}
              onChange={handleChange} />
          </label>
          <label htmlFor="">Shift:
            <input
              type="number"
              pattern='[0-9]'
              name="shift"
              id="shift-num"
              value={shiftEncrypt.shift}
              onChange={handleChange} />
          </label>
          <Button name={'Encrypt'} />
        </form>

        <div>{cipherText.status === 200 ?
          <h3>{cipherText.cipher_text}</h3>
          :
          <h3>Cryptic...Fail</h3>}
        </div>
      </header>

    </div>
  );
}
