import React, { useState } from 'react';
import axios from 'axios'
import '../../App.css';
import Button from '../Button';

function VigenereDecrypt() {
    const [decryptedText, setDecryptedText] = useState({})
    const [vigenereDecrypt, setVigenereDecrypt] = useState({
        ciphertext: '',
        key: ''
    })

    const handleChange = (event) => {
        const { name, value } = event.target;
        setVigenereDecrypt({
            ...vigenereDecrypt,
            [name]: value
        });
    };


    const handleSubmit = (event) => {
        event.preventDefault();
        // const path = 'http://127.0.0.1:5000/vigenere_encrypt'
        const path = 'https://cryptography-web-application.herokuapp.com/vigenere_decrypt'
        axios({
            method: 'POST',
            url: path,
            data: {
                ciphertext: vigenereDecrypt.ciphertext,
                key: vigenereDecrypt.key
            }
        })
            .then(response => {
                console.log("SUCCESS", response)
                setDecryptedText(response)
            }).catch(error => {
                console.log(error)
            })
    }

    return (
        <div>
            <h2>Decrypt</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="">Ciphertext:
                        <input
                            type="text"
                            name="ciphertext"
                            id="cipher-text"
                            value={vigenereDecrypt.ciphertext}
                            onChange={handleChange} />
                    </label>
                </div>
                <div>
                    <label htmlFor="">Key:
                        <input
                            type="text"
                            name="key"
                            id="key-value"
                            value={vigenereDecrypt.key}
                            onChange={handleChange} />
                    </label>
                </div>
                <Button name={'Decrypt'} />
            </form>

            <div>{decryptedText.status === 200 &&
                <div>
                    <h4>Decrypted Text:</h4>
                    <p>{decryptedText.data.plaintext}</p>
                </div>}
            </div>

        </div>
    );
}

export default VigenereDecrypt;
