import './App.css';
import React, { useEffect, useState } from 'react';
import axios from 'axios'

function App() {
  const [message, setMessage] = useState({})
  const [ciphertext, setCiphertext] = useState({})
  const [plain_text, setPlainText] = useState({})
  const [error, setErrorMessage] = useState({})

  // useEffect(() => {
  //   axios.get('https://cryptography-web-application.herokuapp.com/flask/crypto').then(response => {
  //     console.log("SUCCESS", response)
  //     setGetMessage(response)
  //   }).catch(error => {
  //     console.log(error)
  //   })

  let handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let res = await fetch("http://127.0.0.1:5000/flask/crypto", {
        method: "get",
        body: JSON.stringify({
          ciphertext
        }),
      });
      let resJson = await res.json();
      if (res.status === 200) {
        console.log(resJson)
        setPlainText(resJson);

      } else {
        setErrorMessage("Some error occurred");
      }
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/flask/crypto').then(response => {
      console.log("SUCCESS", response)
      setMessage(response)
    }).catch(error => {
      console.log(error)
    })

  }, [])
  return (
    <div className="App">
      <header className="App-header">
        <h1>Cryptography Web App</h1>
        <div>{message.status === 200 ?
          <h3>{message.data.message}</h3>
          :
          <h3>LOADING</h3>}</div>
        <form onSubmit={handleSubmit}>
          <label htmlFor="">
            Enter Ciphertext to decrypt
            <input
              type="text"
              value={ciphertext}
              onChange={(e) => setCiphertext(e.target.value)} />
          </label>
          <button type='submit'>Decrypt</button>
        </form>
        {/* <p>{plain_text}</p> */}
      </header>

    </div>
  );
}

export default App;
